var jsonData = JSON.parse(responseBody);
var guests = JSON.parse(environment['thub_hotel_guests'] || {}); 
var booking  = {
    "checkIn":environment['thub_hotel_check_in'],
    "checkOut":environment['thub_hotel_check_out'],
    "vendor":{
        "id": environment['thub_hotel_vendor_id']
    },
};

var i;
for (i in jsonData.items[0].hotels.items) {
    var item = jsonData.items[0].hotels.items[i];
    var accommodation = item.accommodations.items[0];
    accommodation.guests = guests;
    item.accommodations.items = [accommodation];
    booking.hotel = item;
    postman.setEnvironmentVariable('thub_hotel_booking_' + i, JSON.stringify(booking));
    postman.setEnvironmentVariable('thub_hotel_track_' + i, item.track);
    postman.setEnvironmentVariable('thub_hotel_broker_' + i, item.broker);
}

postman.setEnvironmentVariable('thub_hotel_booking', environment['thub_hotel_booking_0']);
postman.setEnvironmentVariable('thub_hotel_track', environment['thub_hotel_track_0']);
postman.setEnvironmentVariable('thub_hotel_broker', environment['thub_hotel_broker_0']);

